mapper.insert:
INSERT INTO chat_message_delete (
  user_id,
  target_id
) VALUES (
  #{userId,jdbcType=BIGINT},
  #{targetId,jdbcType=BIGINT}
)

mapper.update:
UPDATE chat_message_delete
   SET sign_num = sign_num + 1
 WHERE user_id = #{userId,jdbcType=BIGINT}
   AND target_id  = #{targetId,jdbcType=BIGINT}

mapper.select:
SELECT update_time
  FROM chat_message_delete
 WHERE user_id = #{userId,jdbcType=BIGINT}
   AND target_id  = #{targetId,jdbcType=BIGINT}

mapper.delete:
DELETE FROM chat_message_delete
 WHERE user_id = #{userId,jdbcType=BIGINT}
   AND target_id  = #{targetId,jdbcType=BIGINT}
