mapper.delete:
delete t1 from tb_task_follow t1
where exists (
    select 1 from tb_task t2 where t2.code = split_str(t1.code, '_', 2) and t2.category = 4
)
and t1.create_time  <  #{localDate}

delete t1 from tb_task_reward_record t1
where exists (
    select 1 from tb_task t2 where t2.code = split_str(t1.code, '_', 2) and t2.category in(3, 4)
)
and t1.create_time  <  #{localDate}
