mapper.select:
select

from order_msg


        order_sn = #{orderSn}


        AND order_id = #{orderId}


        AND order_status = #{orderStatus}


        AND rmb_method = #{rmbMethod}


        AND create_time  <  #{createTime}

mapper.update:
update order_msg
set
    is_handle = #{isHandle}
where
    order_sn = #{orderSn}
