mapper.insert:
insert into tb_voice_room_role

    room_id, user_id, role


    #{roomId}, #{userId}, #{role}

mapper.delete:
delete from tb_voice_room_role

    room_id = #{roomId}
    and user_id = #{userId}

        and role = #{role}

mapper.select:
select role
from tb_voice_room_role
where
room_id = #{roomId}
and user_id = #{userId}
